// Slack Î©îÏãúÏßÄÏóêÏÑú MR Î≤àÌò∏ÏôÄ ÌîÑÎ°úÏ†ùÌä∏ Ï∂îÏ∂ú
const body = $input.item.json.body;
const text = body.text || '';

// ÌîÑÎ°úÏ†ùÌä∏ Ìå®ÌÑ¥
const projectPatterns = [
  /\[ÌîÑÎ°úÏ†ùÌä∏:\s*([^\]]+)\]/i,
  /([a-z][a-z0-9-]*-server)/i,
  /([a-z][a-z0-9-]+)\s+!\d+/i
];

let projectHint = null;
for (const pattern of projectPatterns) {
  const match = text.match(pattern);
  if (match) {
    projectHint = match[1].toLowerCase().trim();
    break;
  }
}

// MR Î≤àÌò∏ Ìå®ÌÑ¥
const mrPatterns = [
  /!([0-9]+)/,
  /MR\s*#?([0-9]+)/i,
  /merge_requests\/([0-9]+)/i
];

let mrNumber = null;
for (const pattern of mrPatterns) {
  const match = text.match(pattern);
  if (match) {
    mrNumber = parseInt(match[1]);
    break;
  }
}

if (!mrNumber) {
  return { json: { error: 'MR Î≤àÌò∏Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.', channel: body.channel, threadTs: body.threadTs || body.timestamp } };
}

return {
  json: {
    mr_iid: mrNumber,
    project_hint: projectHint,
    channel: body.channel,
    threadTs: body.threadTs || body.timestamp,
    timestamp: body.timestamp,
    user: body.user,
    text: text
  }
};

// Claude Flow MR Î∂ÑÏÑù API Ìò∏Ï∂ú (Best Practice: Îã®Ïùº Ìò∏Ï∂úÎ°ú Î™®Îì† Ï†ïÎ≥¥)
const extractData = $('Extract MR Number').first().json;
const apiUrl = process.env.CLAUDE_FLOW_API_URL || 'http://host.docker.internal:8080';

// ÌîÑÎ°úÏ†ùÌä∏ Í≤∞Ï†ï (ÌôòÍ≤ΩÎ≥ÄÏàò ÎòêÎäî ÌûåÌä∏)
let project = extractData.project_hint;
if (!project) {
  // GITLAB_GROUP ÌôòÍ≤ΩÎ≥ÄÏàò ÏÇ¨Ïö©
  project = process.env.GITLAB_GROUP || 'sirius/ccds';
}

try {
  // Claude FlowÏùò MR Î∂ÑÏÑù API Ìò∏Ï∂ú (2-Pass ÏïÑÌÇ§ÌÖçÏ≤ò)
  const response = await this.helpers.httpRequest({
    method: 'GET',
    url: `${apiUrl}/api/v1/mr-review/analyze/${encodeURIComponent(project)}/${extractData.mr_iid}`,
    json: true,
    timeout: 60000
  });

  if (!response.success) {
    return { json: { error: response.error || 'MR Î∂ÑÏÑù Ïã§Ìå®', ...extractData } };
  }

  // Î∂ÑÏÑù Í≤∞Í≥º Î∞òÌôò (APIÍ∞Ä Ïù¥ÎØ∏ GitLab ÌîåÎûòÍ∑∏Î•º ÏßÅÏ†ë ÌôúÏö©)
  return {
    json: {
      ...extractData,
      analysisResult: response.data,
      message: response.message
    }
  };
} catch (e) {
  return { json: { error: `MR Î∂ÑÏÑù API Ìò∏Ï∂ú Ïã§Ìå®: ${e.message}`, ...extractData } };
}

// Î∂ÑÏÑù Í≤∞Í≥ºÎ°ú AI Î¶¨Î∑∞ ÌîÑÎ°¨ÌîÑÌä∏ ÏÉùÏÑ±
const data = $input.item.json;
const analysis = data.analysisResult;
const mr = analysis.mr || {};
const fileAnalysis = analysis.fileAnalysis || {};
const quickIssues = analysis.quickIssues || [];
const priorityFiles = analysis.priorityFiles || [];

// Îπ†Î•∏ Ïù¥Ïäà Ìè¨Îß∑ÌåÖ
let issuesText = '';
if (quickIssues.length > 0) {
  issuesText = '\n\n## ÏûêÎèô Í∞êÏßÄ Ïù¥Ïäà (Pass 1)\n';
  for (const issue of quickIssues) {
    issuesText += `${issue.message}\n`;
    if (issue.suggestion) issuesText += `  ‚Üí ${issue.suggestion}\n`;
  }
}

// ÌååÏùº Î∂ÑÏÑù ÏöîÏïΩ
let filesText = '\n\n## Î≥ÄÍ≤Ω ÌååÏùº Î∂ÑÏÑù (GitLab API ÌîåÎûòÍ∑∏ Í∏∞Î∞ò)\n';
filesText += `Ï¥ù ${fileAnalysis.totalFiles || 0}Í∞ú ÌååÏùº (+${fileAnalysis.totalAdditions || 0}/-${fileAnalysis.totalDeletions || 0})\n\n`;

if (fileAnalysis.renamed?.length > 0) {
  filesText += '### ÌååÏùºÎ™Ö Î≥ÄÍ≤Ω (Rename)\n';
  for (const f of fileAnalysis.renamed) {
    filesText += `- ‚úèÔ∏è ${f.oldPath} ‚Üí ${f.newPath}\n`;
  }
}
if (fileAnalysis.added?.length > 0) {
  filesText += '### Ïã†Í∑ú ÌååÏùº (Add)\n';
  for (const f of fileAnalysis.added) {
    filesText += `- ‚ûï ${f.path}\n`;
  }
}
if (fileAnalysis.deleted?.length > 0) {
  filesText += '### ÏÇ≠Ï†ú ÌååÏùº (Delete)\n';
  for (const f of fileAnalysis.deleted) {
    filesText += `- ‚ûñ ${f.path}\n`;
  }
}
if (fileAnalysis.modified?.length > 0) {
  filesText += '### ÏàòÏ†ï ÌååÏùº (Modify)\n';
  for (const f of fileAnalysis.modified.slice(0, 10)) {
    filesText += `- üìù ${f.path} (+${f.additions || 0}/-${f.deletions || 0})\n`;
  }
  if (fileAnalysis.modified.length > 10) {
    filesText += `- ... Ïô∏ ${fileAnalysis.modified.length - 10}Í∞ú\n`;
  }
}

// ÌîÑÎ°¨ÌîÑÌä∏ ÎπåÎìú
let prompt = `## MR !${mr.iid || data.mr_iid} ÏΩîÎìú Î¶¨Î∑∞ ÏöîÏ≤≠\n\n`;
prompt += `**Ï†úÎ™©**: ${mr.title || 'Unknown'}\n`;
prompt += `**Î∏åÎûúÏπò**: \`${mr.source_branch || '?'}\` ‚Üí \`${mr.target_branch || '?'}\`\n`;
prompt += `**ÏûëÏÑ±Ïûê**: ${mr.author || 'Unknown'}\n`;
prompt += `**URL**: ${mr.web_url || ''}\n`;
prompt += filesText;
prompt += issuesText;

if (priorityFiles.length > 0) {
  prompt += '\n\n## Ïö∞ÏÑ† Î¶¨Î∑∞ ÎåÄÏÉÅ ÌååÏùº\n';
  for (const f of priorityFiles.slice(0, 5)) {
    prompt += `- ${f}\n`;
  }
}

prompt += '\n\nÏúÑ Î∂ÑÏÑù Í≤∞Í≥ºÎ•º Î∞îÌÉïÏúºÎ°ú Ï¢ÖÌï©Ï†ÅÏù∏ ÏΩîÎìú Î¶¨Î∑∞Î•º ÏàòÌñâÌï¥Ï£ºÏÑ∏Ïöî.';

return {
  json: {
    ...data,
    formatted_prompt: prompt,
    mr_iid: mr.iid || data.mr_iid,
    mr_title: mr.title || 'Unknown',
    mr_url: mr.web_url || '',
    project_id: data.project_hint
  }
};

// Claude Flow Ïã§Ìñâ (Pass 2: AI Ïã¨Ï∏µ Î∂ÑÏÑù)
const data = $input.item.json;
const apiUrl = process.env.CLAUDE_FLOW_API_URL || 'http://host.docker.internal:8080';

const systemPrompt = `ÎãπÏã†ÏùÄ ÏãúÎãàÏñ¥ ÏΩîÎìú Î¶¨Î∑∞Ïñ¥ÏûÖÎãàÎã§.

## Î¶¨Î∑∞ ÏßÄÏπ®
1. Pass 1ÏóêÏÑú ÏûêÎèô Í∞êÏßÄÎêú Ïù¥ÏäàÎ•º Í≤ÄÌÜ†ÌïòÍ≥† ÌÉÄÎãπÏÑ±ÏùÑ ÌèâÍ∞ÄÌïòÏÑ∏Ïöî
2. ÏΩîÎìú ÌíàÏßà, ÏïÑÌÇ§ÌÖçÏ≤ò, ÏÑ±Îä• Í¥ÄÏ†êÏóêÏÑú Ï∂îÍ∞Ä ÌîºÎìúÎ∞±ÏùÑ Ï†úÍ≥µÌïòÏÑ∏Ïöî
3. Breaking ChangeÍ∞Ä ÏûàÎã§Î©¥ Î∞òÎìúÏãú Ïñ∏Í∏âÌïòÏÑ∏Ïöî
4. Í∏çÏ†ïÏ†ÅÏù∏ Ï∏°Î©¥ÎèÑ Ïñ∏Í∏âÌïòÏÑ∏Ïöî

## ÏùëÎãµ ÌòïÏãù (JSON)
{
  "verdict": "approve" | "request_changes" | "comment",
  "summary": "1-2Î¨∏Ïû• ÌïúÍ∏Ä ÏöîÏïΩ",
  "points": ["ÌîºÎìúÎ∞± Ìï≠Î™© Î∞∞Ïó¥"],
  "gitlab_comment": "GitLab ÏΩîÎ©òÌä∏Ïö© ÎßàÌÅ¨Îã§Ïö¥"
}`;

try {
  const response = await this.helpers.httpRequest({
    method: 'POST',
    url: `${apiUrl}/api/v1/execute-with-routing`,
    body: {
      prompt: data.formatted_prompt,
      systemPrompt: systemPrompt,
      maxTurns: 15,
      channel: 'gitlab-review-v2',
      source: 'slack_mr'
    },
    json: true,
    timeout: 600000
  });

  return { json: { ...data, claudeResponse: response } };
} catch (e) {
  return { json: { ...data, claudeResponse: { success: false, error: e.message } } };
}

// Î°úÎî© Î∞òÏùë Ï†úÍ±∞
const data = $input.item.json;
const apiUrl = process.env.CLAUDE_FLOW_API_URL || 'http://host.docker.internal:8080';

try {
  await this.helpers.httpRequest({
    method: 'POST',
    url: `${apiUrl}/api/v1/slack/reaction`,
    body: { channel: data.channel, timestamp: data.timestamp, emoji: 'hourglass_flowing_sand', remove: true },
    json: true
  });
} catch (e) { /* ignore */ }

return { json: data };

// Claude ÏùëÎãµ ÌååÏã±
const data = $input.item.json;
const rawResult = data.claudeResponse?.result || '';
const analysis = data.analysisResult || {};
const quickIssues = analysis.quickIssues || [];

// ÏïàÏ†ÑÌïú Î¨∏ÏûêÏó¥/Î∞∞Ïó¥ Î≥ÄÌôò
const safeString = (val, fallback = '') => {
  if (val === undefined || val === null) return fallback;
  if (typeof val === 'object') return JSON.stringify(val);
  return String(val);
};
const safeArray = (val) => {
  if (!val || !Array.isArray(val)) return [];
  return val.map(item => safeString(item)).filter(s => s.length > 0);
};

let review = {
  verdict: 'comment',
  summary: 'Î¶¨Î∑∞ ÏôÑÎ£å',
  points: [],
  gitlab_comment: '## AI Code Review\n\n' + rawResult
};

try {
  const jsonMatch = rawResult.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    const parsed = JSON.parse(jsonMatch[0]);
    review = {
      verdict: safeString(parsed.verdict, 'comment'),
      summary: safeString(parsed.summary, 'Î¶¨Î∑∞ ÏôÑÎ£å'),
      points: safeArray(parsed.points),
      gitlab_comment: safeString(parsed.gitlab_comment, '## AI Code Review\n\n' + rawResult)
    };
  }
} catch (e) { /* JSON ÌååÏã± Ïã§Ìå® Ïãú ÏõêÎ≥∏ ÏÇ¨Ïö© */ }

// Pass 1 Ïù¥ÏäàÎ•º GitLab ÏΩîÎ©òÌä∏Ïóê Ï∂îÍ∞Ä
let pass1Section = '';
if (quickIssues.length > 0) {
  pass1Section = '\n\n### ÏûêÎèô Í∞êÏßÄ Ïù¥Ïäà (Pass 1)\n';
  for (const issue of quickIssues) {
    pass1Section += `- ${issue.message}\n`;
  }
}

if (!review.gitlab_comment.includes('ÏûêÎèô Í∞êÏßÄ')) {
  review.gitlab_comment = review.gitlab_comment + pass1Section;
}
review.gitlab_comment += '\n\n---\n_Generated by Claude Flow V2 (Best Practice)_';

// Slack Î©îÏãúÏßÄ ÎπåÎìú
const verdictEmoji = { approve: ':white_check_mark:', request_changes: ':warning:', comment: ':speech_balloon:' };
const emoji = verdictEmoji[review.verdict] || ':speech_balloon:';
const mrUrl = safeString(data.mr_url, '#');
const mrIid = safeString(data.mr_iid, '?');
const mrTitle = safeString(data.mr_title, 'Unknown MR');

let slackMessage = `${emoji} <${mrUrl}|!${mrIid}> ${mrTitle}\n${review.summary}`;
const points = review.points.slice(0, 5).map(p => `‚Ä¢ ${p}`).join('\n');
if (points) slackMessage += '\n' + points;

// Pass 1 Ïù¥Ïäà Ïàò ÌëúÏãú
if (quickIssues.length > 0) {
  slackMessage += `\n\nüìä ÏûêÎèô Í∞êÏßÄ: ${quickIssues.length}Í∞ú Ïù¥Ïäà`;
}

return { json: { ...data, review, slackMessage } };

// ÏµúÏ¢Ö ÏôÑÎ£å: GitLab ÏΩîÎ©òÌä∏ + Slack Î©îÏãúÏßÄ + ÏÑ±Í≥µ Î∞òÏùë
const data = $input.item.json;
const gitlabUrl = process.env.GITLAB_URL || 'https://gitlab.example.com';
const gitlabToken = process.env.GITLAB_TOKEN || '';
const apiUrl = process.env.CLAUDE_FLOW_API_URL || 'http://host.docker.internal:8080';

// 1. GitLab ÏΩîÎ©òÌä∏
if (data.project_id && data.mr_iid) {
  try {
    await this.helpers.httpRequest({
      method: 'POST',
      url: `${gitlabUrl}/api/v4/projects/${encodeURIComponent(data.project_id)}/merge_requests/${data.mr_iid}/notes`,
      headers: { 'PRIVATE-TOKEN': gitlabToken },
      body: { body: data.review.gitlab_comment },
      json: true
    });
  } catch (e) { console.log('GitLab comment error:', e.message); }
}

// 2. Slack Î©îÏãúÏßÄ
try {
  await this.helpers.httpRequest({
    method: 'POST',
    url: `${apiUrl}/api/v1/slack/message`,
    body: { channel: data.channel, text: data.slackMessage, threadTs: data.threadTs },
    json: true
  });
} catch (e) { console.log('Slack message error:', e.message); }

// 3. ÏÑ±Í≥µ Î∞òÏùë
try {
  await this.helpers.httpRequest({
    method: 'POST',
    url: `${apiUrl}/api/v1/slack/reaction`,
    body: { channel: data.channel, timestamp: data.timestamp, emoji: 'white_check_mark', remove: false },
    json: true
  });
} catch (e) { console.log('Reaction error:', e.message); }

return { json: { success: true } };
